import React, {useState, ChangeEvent } from 'react';
import { Typography, Box, FormControl, InputLabel, MenuItem, Select } from '@mui/material';
import Theme from '../../themes/theme';
import Background from '../../assets/background.jpg';
import AddBoxIcon from "@mui/icons-material/AddBox";
import SearchBar from '../searchbar';

// Custom MenuProps to change the height of the dropdown menu
const customMenuProps = {
    PaperProps: {
        style: {
            maxHeight: '20px', // Adjust the height here
            width: 250,
        },
    },
};

const Main: React.FC = () => {
    const [searchQuery, setSearchQuery] = useState('');
    const [dropdownValue, setDropdownValue] = useState('');

    const handleSearchChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        console.log()
    };

    const handleDropdownChange = (event: ChangeEvent<{ value: unknown }>) => {
        setDropdownValue(event.target.value as string);
    };

    return (
        <Box sx={{
            padding: '20px',
            display: 'flex',
            flexDirection: 'column',
            width: '90%',
            gap: 3
        }}>
            <Box sx={{
                backgroundImage: `url(${Background})`,
                backgroundSize: 'cover',
                borderRadius: '15px',
                padding: '20px',
            }}>
                <Box sx={{
                    backgroundColor: Theme.palette.secondary.main,
                    width: '280px',
                    borderRadius: '15px',
                    padding: '20px',
                    paddingTop: '100px',
                    display: 'flex',
                    flexDirection: 'row',
                    justifyContent: 'space-between',
                    alignItems: 'flex-end'
                }}>
                    <Box>
                        <Typography variant='h2' sx={{
                            fontWeight: 'bold',
                            fontSize: '25px'
                        }}>
                            Voice Generation
                        </Typography>
                        <Box sx={{
                            marginTop: '10px',
                            width: '170px',
                            textAlign: 'justify'
                        }}>
                            <Typography variant='subtitle2' sx={{
                                fontSize: '10px'
                            }} >
                                A dialog is a type of modal window that appears in front of app content to provide critical information, or prompt for a decision to be made.
                            </Typography>
                        </Box>
                    </Box>
                    <AddBoxIcon sx={{ fontSize: "70px" }} />
                </Box>
            </Box>
            <Box sx={{
                display: 'flex',
                flexDirection: 'row'
            }}>
                <Typography variant='h2' sx={{
                    fontWeight: 'bold',
                    fontSize: '25px'
                }}>
                    Your projects
                </Typography>
                <Box sx={{
                    backgroundColor: 'black',
                    color: 'white',
                    width: '40px',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    borderRadius: '5px',
                    marginLeft: '10px',
                    marginRight: 'auto',
                    padding: '2px 0'
                }}>
                    <Typography variant='h2' sx={{ fontWeight: 'bold', fontSize: '20px' }}>0</Typography>
                </Box>
                <Box>
                    <SearchBar
                        placeholder='Search'
                        onChange={handleSearchChange}
                        searchBarWidth='200px'
                    />
                </Box>
                <FormControl sx={{
                    marginLeft: '10px',
                    minWidth: 100
                }}>
                    <InputLabel id="dropdown-label">Sort By</InputLabel>
                    <Select
                        labelId="dropdown-label"
                        value={dropdownValue}
                        // onChange={handleDropdownChange}
                        label="Select"
                        MenuProps={customMenuProps}
                    >
                        <MenuItem value={10}>Ascending</MenuItem>
                        <MenuItem value={20}>Descending</MenuItem>
                    </Select>
                </FormControl>
            </Box>
        </Box>
    );
};

export default Main;
