import React from 'react';
import MenuIcon from '@mui/icons-material/Menu';
import AddBoxIcon from '@mui/icons-material/AddBox';
import StarsIcon from '@mui/icons-material/Stars';
import HelpIcon from '@mui/icons-material/Help';
import Avatar from '../../assets/avatar.png';
import {Link as RouterLink, useLocation} from "react-router-dom";
import {Box, Hidden, Typography, Link as MuiLink} from "@mui/material";
import Theme from '../../themes/theme';

const navLinks = [
    {
        name: "Explore",
        icon: <StarsIcon />,
        link: '/'
    },
    {
        name: "Gallary",
        icon: <StarsIcon />,
        link: '/'
    },
    {
        name: "Museum",
        icon: <StarsIcon />,
        link: '/'
    },
    {
        name: "About us",
        icon: <StarsIcon />,
        link: '/'
    },
]

const Sidebar = () => {
    const {pathname} = useLocation()
    return (
        <Box sx={{
            backgroundColor: Theme.palette.secondary.main,
            padding: 0,
            borderRadius: 2,
            display: "flex",
            flexDirection: {
                sx: "column",
                lg: "column"
            },
            alignItems: "center",
            width: {
                sm: "100%",
                lg: 80
            }
        }}>
            <Box sx={{
                py: {
                    xs: "0px",
                    lg: "16px"
                },
                display: "flex",
                flexDirection: {
                    xs: "column",
                    lg: "column"
                },
                gap: 2,
                justifyContent: "space-between",
                alignItems: {
                    xs: "center",
                    ls: "start"
                },
                width: "100%",
                flexGrow: 1
            }}>
                <Box sx={{
                    display: "flex",
                    gap: 2,
                    alignItems: "center",
                    flexDirection: {
                        xs: "column",
                        lg: "column"
                    },
                }}>
                    <img src={Avatar} alt="avatar" style={{width: "40px", height: "40px", borderRadius: "50%"}}/>
                    <MenuIcon sx={{ fontSize: "20px" }} />
                    <AddBoxIcon sx={{ color: Theme.palette.primary.main, fontSize: "50px" }} />
                    {navLinks.map((item) => (
                        <MuiLink
                            component={RouterLink}
                            key={item.name}
                            to={item.link}
                            style={{textDecoration: "none", color: Theme.palette.text.primary}}
                        >
                            <Box sx={{
                                display: "flex",
                                flexDirection: {
                                    sx: "column",
                                    lg: "column"
                                },
                                alignItems: "center",
                                color: Theme.palette.text.primary,
                                textDecoration: "none"
                            }}>
                                <item.icon.type
                                    sx={{
                                        color: Theme.palette.text.primary,
                                        width: "15px"
                                    }}
                                />
                                <Typography variant='subtitle1' sx={{ fontWeight: 'bold', fontSize: '13px' }}>{item.name}</Typography>
                            </Box>
                        </MuiLink>
                    ))}
                </Box>
                <Box>
                    <HelpIcon sx={{
                        color: Theme.palette.text.primary,
                        width: "35px"
                    }}/>
                </Box>
            </Box>
        </Box>
    );
};

export default Sidebar;
